INSERT INTO `__PREFIX__wwh_news` (`id`, `newscategoryid`, `newsname`, `n_keywords`, `n_description`, `summary`, `tjdata`, `image`, `weigh`, `time`, `content`, `views`, `updatetime`) VALUES
(1, 1, '元气森林：今年底将有三个自建工厂开建', '关键字1,关键字2,关键字3', '说明演示文字1', '9月8日，元气森林相关负责人向北京商报记者透露，今年底将有三个自建工厂同时动工开建，这三个工厂分别在滁州、天津、广州三地。', '1', 'http://demo.wuwenhui.cn/uploads/20200909/558bb6e15213d02995fd2a793e25d86d.jpg', 1, '2020-09-08', '<p style=\"line-height: 2;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">北京商报讯（记者 钱瑜 张君花）9月8日，元气森林相关负责人向北京商报记者透露，今年底将有三个自建工厂同时动工开建，这三个工厂分别在滁州、天津、广州三地。该负责人还表示，目前滁州自建工厂的产能占比为三分之一，希望元气森林不断加大投入自建工厂，未来产能满足率能达80%左右。</span></p><p style=\"line-height: 2;\"><br><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">相关资料显示，2015年，元气森林研发中心成立；2015-2016年，元气森林进入产品研发阶段，并正式成立公司；2017-2018年，元气森林产品正式面向市场和消费者，推出无糖苏打气泡水，主打“0糖、0脂肪、0卡路里”标签。数据显示，截至2019年底，元气森林入驻全国400多个便利系统、超过8万家高端商超和便利店。同时，在线上覆盖天猫、京东等平台。</span></p>', 13, 1617244934),
(2, 1, '为什么越发达的国家，越不流行移动支付？“无现金时代”有多危险', '关键字1,关键字2,关键字3', '说明演示文字2', '在中国这样的现象司空见惯：大到公司，小到摊贩，几乎人人必备支付宝。\"支付宝支付\"和\"微信支付\"在国内的普及让日本人非常羡慕，在中国买东西就是\"一部手机闯天下\"。', '0', 'http://demo.wuwenhui.cn/uploads/20200909/aa47b1720fc02cd704edddb828673702.jpg', 1, '2020-09-08', '<p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782969/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在中国这样的现象司空见惯：大到公司，小到摊贩，几乎人人必备支付宝。\"支付宝支付\"和\"微信支付\"在国内的普及让日本人非常羡慕，在中国买东西就是\"一部手机闯天下\"。可见作为一个正在发展中的国家，中国的移动支付是远远领先于世界很多国家的。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">按照一贯的发展趋势，发达国家应当比我们更早普及移动支付，进入到\"无现金时代\"。事实却是除了芬兰，瑞典等北欧高达发达的资本主义国家或将进入\"无现金时代\"之外，美国，日本这些发达国家的民众依然习惯于在交易过程中使用现金或信用卡。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">于是，有人提出了这样一种假设：越发达的国家，越不流行移动支付。这个假设又是否成立呢？</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">一 移动支付流行的民众基础</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">中国的移动支付最早是在2004年，阿里巴巴成立了支付宝，用以给电商交易提供信用中介服务。6年后，第三方支付合理化，进而在国家银行的支持下，第三方移动支付普及，这才产生了中国的\"二维码文化\"。而这一过程中最重要的催化剂就是——中国人的接受程度。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782978/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在此之前，没有人想到有一天买东西可以不用现金，只需要手里的二维码。移动支付是引领一种支付理念。而这种理念出现以后，80%的人中国人是持支持态度的，扫码又酷又省事，花钱没有感觉不心疼，多好。当然，剩余的20%是不怎么会使用手机的老年人。移动支付迅速走红中国。所以我们说，移动支付的流行需要的是认同。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">二 发达国家不流行移动支付？</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">来自于科学日报的数据，丹麦、挪威和瑞典这几个发达国家都位列\"非现金化\"的前列。据 CNN 报道，挪威央行的数据显示，斯堪的纳维亚人的所有支付中，只有不到 6% 是通过现金完成的。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782975/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">而很多人提出\"越是发达国家，越不流行移动支付\"这一假设无疑是因为美国和日本的支付方式中现金占比居高不下，正如美国使用现金比例高达 47%。那么是什么原因让美国这样一个发达国家移动支付还没有中国普及呢？</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">首要原因是美国和中国支付体系的不同。中国的支付是从纸币到移动支付，信用卡在其中只是辅助作用。而美国则不同，美国拥有一套完整的信用卡支付系统，信用支付制度在发达国家有着超过百年的历史，信用记录是需要长期积累的，积累信用的一个重要途径是——信用卡消费。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">美国鼓励居民使用信用卡，将信用卡的使用情况与个人征信紧密结合。在美国民众的心目中，信用卡支付是最为便捷和带来良好信用的最佳选择。早在中国的支付宝之前，苹果公司就已经研究出了移动支付手段—— APPLE pay。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782977/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">但是信用制度根深蒂固的，社会对于APPLE pay接受程度很低，最后也没能让移动支付在美国大规模普及。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">其次便是美国历史遗留的\"金钱泡沫局\"的心理阴影。作为一个200年迅速发展的年轻国家，加之战争频繁，美国人心中是对金钱缺乏安全感的。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">包括二战之后要建立以美国为主导，黄金与美元挂钩的体系，有很大一部分原因就是害怕\"金钱的泡沫局\"。美国人希望将钱握在手中，才会感到踏实。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">而采用移动支付，则会让金钱进一步虚拟化，\"无现金时代\"更是美国人不期望到来的一种的形式。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782976/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">三 无现金社会等同于\"捆绑自由\"，发达国家无法接受</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在美，日，德这些发达国家移动支付并未流行，也是有着历史原因的。从美国独立宣言开始，自由与人权是极为被看重的。无法想象一个美国人发现大数据随时可以追踪他的消费记录，消费地点时会有多么惊慌失措，对于他们来讲，这无疑是对于个人隐私权的一种亵渎。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">另外日本社会使用现金的习惯根深蒂固：不仅有基本无伪造，ATM多使用方便，遭盗窃的少，算账不容易出错等等优点。而且现金带来的\"满足感\"是拿着一部手机所不能给予的。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">尽管日本政府大力支持移动支付并且给予一定优惠政策，但是难以改变这种观念带来的选择。如果进入到\"无现金社会\"，相当于进入到一个\"无隐私社会\"\"无自由社会\"，这是很难转变的观念。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782970/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">四 发达国家移动支付未流行， 是因为无现金时代危险重重？</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">有人猜测：发达国家移动支付未大规模流行，是否基于无现金时代的危险性。移动支付带给生活的便利性大家有目共睹，另外移动支付减少人与人的直接接触，减少细菌传播和病毒的扩散。但不得不承认，当无现金时代到来的那一天，社会是会存在着支付风险的。隐私的泄露，频繁的诈骗都将会是社会常态。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">但是对于发达国家和发展中国家，这些危险是共存的，所以并不存在着是因为危险而未流行。除了上述几个原因外，区别还在于中国市场上支付宝与微信和谐共生，并且投入成本低。只要一部手机，人人可以安装支付宝与微信进行支付。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">但是，苹果研究出来的APPLE pay 就有着局限性，要使用APPLE pay进行支付，首先要有一部 iPhone 6 以上的机型。为了实现移动支付而去重新购买高价手机对于普通美国民众而言是不乐意的。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430782980/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">另外美国不像中国对于欠发达地区是采取扶持政策，移动支付需要强大的网络基站作为基础，而美国基站的建设是有选择性的，无法带来商业性的基站建设投入是不划算的，所以基站建设也是不平衡的，这就造成了移动支付在美国未能普遍化的局面。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">究竟谁能成为第一个进入无现金时代的国家？决定因素不在于是否是发达国家，而是一个国家的历史文化，社会基础和基站建设能力所共同决定的。无现金时代虽然有其危险性，但它更是人类智慧的体现。我们有希望进入一个优质的无现金时代。</span></p>', 2, 1617244943),
(3, 1, '如何防止大数据时代个人隐私的“裸奔”？', '关键字1,关键字2,关键字3', '说明演示文字3', '21世纪是数据信息飞速发展的时代，移动互联网、物联网、社交网络、电子商务等极大拓展了互联网的边界和应用范围，各种数据正在快速膨胀变大，整个社会已经步入大数据时代。', '0', 'http://demo.wuwenhui.cn/uploads/20200909/74e684f1c180901d0a70017fc815dd88.jpg', 1, '2020-09-08', '<p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">21世纪是数据信息飞速发展的时代，移动互联网、物联网、社交网络、电子商务等极大拓展了互联网的边界和应用范围，各种数据正在快速膨胀变大，整个社会已经步入大数据时代。但大数据是把双刃剑，国家和企业因大力推动大数据创新应用而获益的同时，也给个人信息安全尤其是隐私信息的安全带来巨大威胁。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12431453689/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">个人隐私在大数据环境下的“裸奔”</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">个人信息泄露的泛滥。在互联网大数据环境下，数据背后蕴含着巨大价值，不法之徒肆无忌惮地收集和使用个人信息，导致个人隐私泄露的泛滥。在网上，只要输入“求购个人信息”，搜索结果中立刻会出现大量售卖个人信息的网站、QQ群或个人联系电话，交易的类型包括学生、股民、产妇、幼儿园等，交易的信息包括电话号码、银联账号和密码、购物记录信息等。这些个人隐私信息在“网络黑市”打包出售，且形成了“源头—中间商—非法使用人员”的“黑色产业链”，威胁个人安全。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">精准电信网络诈骗案件频发。在大数据时代下，由于个人信息泄露严重，不法分子不再是“广撒网”式的“盲骗”，而是通过不法渠道获得详细的个人信息，进行“私人定制”的精准诈骗。精准诈骗已经成为电信网络诈骗的新趋势，而且这种“量身定做”的骗术很难被受骗者识破。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">信息集成化带来的潜在风险更大。随着大数据、云计算的大规模普及，阿里云、百度云、腾讯云等各种云存储模式被政府和企业广泛使用。一方面，这有效解决了各终端存储空间不足、不同设备之间难以实现资源共享等问题;另一方面，这种云存储模式却不同程度存在企业和个人信息泄露的风险。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12431453700/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">大数据时代个人隐私保护面临的突出问题</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">第一，个人信息数据规模高速增长、数据特征错综复杂。个人信息具有多维性，如身份特征、财务状况、活动轨迹等信息，这些个人信息还与政府、企业、网站平台以及其他的个人信息相互关联，通过数据挖掘和分析，使这些数据信息具有了极高的价值。目前，在大数据时代下，个人信息的收集方式、传播渠道及使用后果日趋失控，个人信息安全问题比较严峻，有效保护难度较大。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">第二，网络诈骗“黑色产业链”规模庞大、分工专业化。新型网络骗术层出不穷的一个重要原因是网络诈骗“黑色产业链”的存在，随着电信诈骗的形式日益复杂多样，这条“黑色产业链”呈现出跨行业、跨地域、碎片化等新特征，给政府集中、专项打击行动带来困难。而且这条“黑色产业链”因其成本低、回报高，得以野蛮滋生扩张，导致反网络欺诈面临诸多的困境。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">第三，网络诈骗技术、手段不断翻新，侦查打击难度大。网络诈骗“黑色产业链”中的违法犯罪人员掌握先进技术，运用“互联网+”、大数据、云计算、移动互联网等最新的网络技术，诈骗活动涉及领域广泛、诈骗手段不断翻新。尽管我国侦查技术有了很大提高，但仍明显滞后于新兴网络技术的快速发展，而且网络监督机制不够健全和完善，跨区调查取证、协同侦破办案渠道不畅，侦破效率低，跨国界侦查更是困难重重，这些都增加了网络诈骗的侦查打击难度。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">第四，基层政府的治理模式滞后，对侵犯个人信息安全压制力不足。目前造成个人信息泄露原因主要有：黑客组织或个人通过挖掘计算机信息系统安全漏洞，入侵各类数据库系统盗取个人数据信息;掌握数据信息的内部人员与外部不法分子勾结盗取和贩卖个人信息;一些网络服务商通过各种软件，未经客户授权擅自采集、过度披露个人数据信息等。面对拥有先进技术、规模和体系庞杂的网络诈骗“黑色产业链”，基层政府及相关部门仍旧采用局部对抗、个案打击的传统模式，难以有效打击网络诈骗“黑色产业链”，间接加剧了当前的严峻形势。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12431453693/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">以“五个结合”提高网络隐私安全保护工作的实效性</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">把保障大数据健康发展与保护个人隐私安全相结合。目前我国大数据产业处于起步发展阶段，大数据中最具经济价值的个人信息正成为各方争夺的焦点，而隐私保护粗放滞后，导致我国个人信息滥采滥用、隐私泄露泛滥的现状日益严重，但过于严格和机械的法律隐私保护，势必使大数据的开放共享受限，制约大数据产业发展的积极性。因此，平衡好大数据应用和个人信息保护的关系，成为大数据产业健康发展的关键。我国可以借鉴国外的大数据发展与个人隐私保护模式，结合新时期我国大数据战略发展需求以及隐私泄露现状，平衡隐私保护与数据拥有者权益之间的双重需求，构建兼顾安全与发展、符合隐私保护和大数据发展需求的保障体系，实现大数据应用与个人隐私安全的双赢。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在个人信息安全模式上实现技术支持、制度保障、社会共治相结合。对于个人信息安全，技术保护是基础前提、制度规范是根本保障、共治共管是运行模式。物联网、移动互联网、人工智能等新一代信息技术的迅猛发展，为大数据收集和应用提出新的安全挑战。国家和社会各界应充分重视个人信息保护技术的研发和创新，加大资金投入，加强尖端技术人才培养，提高个人信息安全技术，在充分保护个人信息的同时，确保数据信息的正常采集、流通和应用。同时，加快建立统一规范的网络认证标准体系，完善公民隐私和个人信息安全保护机制，加大对信息泄露等恶意违法行为的查处力度，有效保障个人信息安全。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">加大打击力度与完善个人信息保护的相关立法相结合。政府及相关部门对非法泄露和买卖个人信息、电信诈骗等新型网络犯罪应加大打击力度，尤其对侵犯我国网络信息的境内外黑客组织或个人，过度披露数据信息的行为，应开展专项打击。面对日益严重的个人信息泄露和安全威胁，我国应加快制定和完善相关法律法规并尽快制定专门的《个人信息保护法》，详细规范政府部门、企事业单位收集、使用、公开个人信息规则，强化执法管理和监督。同时，借鉴发达国家较成熟的个人信息保护模式，构建一套适合我国国情、网情，切实可行的个人信息安全保护法律体系，有效实现隐私权的保护。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12431453697/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">加强行业自律建设与健全行业标准相结合。随着我国个人信息安全问题日益加剧，加强行业自律、制定行业内部标准迫在眉睫。由政府牵头并监督重点行业，加快制定建议性的行业指导、统一规范网络隐私认证、责任界定等有关标准;加强对相关行业使用个人信息的规范管理，清晰界定个人信息泄露法律责任和义务;加快完善相关岗位工作人员的规范管理和有效监督机制，营造良好的个人隐私保护氛围;倡导行业自律，以行业自律来实现个人隐私保护。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">加强重点保护与提高全民的个人信息保护意识相结合。大数据时代下的个人隐私保护，是由政府或社会团体牵头多方参与，对未成年人、学生、残疾人群等特殊群体以及教育培训、金融理财、房地产等行业的个人信息加以重点监管和保护。此外，政府还需进一步出台有效的强制措施，保障公民对个人信息处理有知情权和决定权，提高个人信息保护意识。同时，在全社会范围内广泛宣传个人信息保护的重要性及个人隐私泄露的严重危害，提高民众对网络诈骗行为的鉴别能力和防范意识，增强全民网络安全意识。</span></p>', 3, 1617244950);
INSERT INTO `__PREFIX__wwh_news` (`id`, `newscategoryid`, `newsname`, `n_keywords`, `n_description`, `summary`, `tjdata`, `image`, `weigh`, `time`, `content`, `views`, `updatetime`) VALUES
(4, 1, '思科前工程师辞职后删库，导致16000个视频会议账号停用', '关键字1,关键字2,关键字3', '说明演示文字4', '一名前思科工程师承认非法访问思科网络并删除456个虚拟机，并导致超过16,000个Webex Teams账户中断。', '1', 'http://demo.wuwenhui.cn/uploads/20200909/2d9ecfde4cac93ce09297e3243907831.jpg', 1, '2020-09-09', '<p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">一名前思科工程师承认非法访问思科网络并删除456个虚拟机，并导致超过16,000个Webex Teams账户中断。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">据美国司法部称，Sudhish Kasaba Ramesh已被指控在未经授权的情况下故意访问受保护的计算机并不顾后果地造成损害，已在圣何塞的一家联邦法院接受了认罪协议。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430429237/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">这位30岁的工程师于2018年4月辞职，但在离开公司大约五个月后选择访问思科的Amazon Web Services（AWS）环境，已部署删除456个虚拟机（VM）的代码。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">2018年9月24日，该代码从Ramesh的Google Cloud Project帐户中启动。作为此操作的代价，两个星期内，超过16,000个Webex Teams账户被停用。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430429225/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">美国检察官说，这家科技巨头需要额外支付140万美元的成本来恢复和纠正对系统造成的损害，并向受网络问题影响的客户退款约100万美元。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">美国司法部说：“他承认他在部署代码时不计后果，并且无视其行为可能损害思科的巨大风险。”</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">工程师于2020年7月13日被正式指控，目前已保释。拉梅什面临长达五年的监禁和高达25万美元的罚款。量刑定于2020年12月9日。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430429232/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">思科在一份声明中表示，已经实施了更多的保护措施，并且公司会尽快处理损失。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">思科说：“我们直接将此问题带给了执法部门，并赞赏他们将这个人绳之以法的伙伴关系。” “我们有信心可以防止再次发生的程序。”</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在相关新闻中，本周早些时候，思科收购了BabbleLabs公司，该公司专门研究旨在减少背景噪音的技术。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">噪音和环境干扰可能会非常令人烦恼，在当今许多人由于冠状病毒大流行而在家工作的时候，远程电话会议工具对于保持企业运营至关重要。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">BabbleLabs的技术将集成到包括Webex Meetings在内的思科协作产品中。</span></p>', 5, 1617244956),
(5, 1, '华为开发者大会发布鸿蒙2.0，鸿蒙拿什么和安卓一战？', '关键字1,关键字2,关键字3', '说明演示文字5', '今年9月10日，华为将会召开HDC开发者大会，在这一次的大会上华为将会发布最新的鸿蒙版本，同时根据余承东公开表示，鸿蒙手机也将会在明年推出。', '0', 'http://demo.wuwenhui.cn/uploads/20200909/fbd958f4fe3d94be1714b0b561e4e12f.jpg', 1, '2020-09-09', '<p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">今年9月10日，华为将会召开HDC开发者大会，在这一次的大会上华为将会发布最新的鸿蒙版本，同时根据余承东公开表示，鸿蒙手机也将会在明年推出。同时根据产业链的消息，华为麒麟9000备货在1000万，可以支撑半年左右的时间。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430354696/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">鸿蒙其实去年就已经发布了，当时的鸿蒙成为1.0版本，这个版本的额鸿蒙只是搭载在了智慧屏上面，而现在的鸿蒙2.0将会搭载在国产PC、手表、车机上。但很遗憾鸿蒙手机依然没有，华为方面可能也在蓄势。而华为消费者CEO余承东也表示了，今年没有鸿蒙手机的计划，明年才会有，鸿蒙手机依旧难产。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430354708/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在鸿蒙发布的时候，提到了两个概念，那就是鸿蒙兼容全部安卓应用，同时鸿蒙要比安卓快60%。那么为什么鸿蒙能够兼容安卓应用？首先安卓基于linux内核，而鸿蒙也是，所以兼容安卓应用成为了可能。另外安卓是在java虚拟机上运行，而鸿蒙不用经过虚拟机，所以鸿蒙更快，这是鸿蒙的优势。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430354725/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">同时鸿蒙也具备其他的优势，安卓是前一代的产物，安卓系统由于经过了这么多年的发展，其除了积累的大量的生态之外，代码也非常的冗长，这增加了安卓系统优化的难度的。而鸿蒙系统是基于如今万物互联时代所诞生的操作系统，早前是硬件配合操作系统，而如今是鸿蒙适配硬件，鸿蒙操作系统的适用性就更广，同时功耗控制、运行速度等等也都有所提升。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430354704/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">但借此鸿蒙依然难以和安卓一战，因为安卓系统有超过230万的生态应用，而鸿蒙呢？这个刚刚起步的新系统啥都没有。或许有网友会说，鸿蒙系统不是兼容所有安卓应用吗？是兼容，但是安卓应用都是专门为了安卓系统进行适配的，并非专门为了鸿蒙系统进行适配，因此就算鸿蒙系统能够兼容安卓，那么稳定性呢？</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430354709/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">很显然兼容安卓应用并不能成为鸿蒙系统的全部生态，同时也不能成为鸿蒙与安卓一战的依仗。鸿蒙还是需要拥有完全属于自己的生态，因此HMS诞生了。HMS是华为自己推出的生态系统如今这个生态系统已经拥有了超过160万的开发，同时拥有了超过8.1万的生态应用。当然这对于安卓系统来说还是小巫见大巫，如果鸿蒙系统能够彻底解决生态问题，那么鸿蒙才堪堪有和安卓一战的实力。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12430354734/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在华为之前，已经有了不少的前车之鉴，诺基亚塞班系统、三星的Bada、微软的windows phone、黑莓的BlackBerryOS等等都是昙花一现，最终都失败在了生态上面。那么华为鸿蒙是否会在这方面也失败呢？你觉得呢？</span></p>', 0, 1617244961),
(6, 2, '美国式5G诈胡：普及的网速慢，高速的不普及，升级后有时还不如4G', '关键字1,关键字2,关键字3', '说明演示文字6', '美国近期关于5G通讯是捷报频传：周一的消息是韩国三星获得了美国运营商Verizon的66亿美元大单。据称三星的所有旗舰产品现在都配备了5G。', '0', 'http://demo.wuwenhui.cn/uploads/20200909/63528f6c2e5350c558c0a909b635a2d7.jpg', 1, '2020-09-09', '<p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">美国近期关于5G通讯是捷报频传：周一的消息是韩国三星获得了美国运营商Verizon的66亿美元大单。据称三星的所有旗舰产品现在都配备了5G。苹果也准备好了，将在9月15日的活动上发布首款支持5G的iPhone。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12433875894/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">从3G发图片、4G发视频的时代过来的消费者，有理由期待5G的更优表现。然而美国的运营商们却在对消费者们打马虎眼：在三大运营商AT&amp;T、T-Mobile和Verizon中，AT&amp;T和T-Mobile的说辞都很小心翼翼。AT&amp;T说5G意味着“提高速度”，而T-Mobile说的是“更快的速度”，都没有给出太多细节。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">IHS Markit旗下的网络分析公司RootMetrics表示，今年上半年，AT&amp;T 5G网速的中位数为46Mbps，仅略高于43Mbps的4G LTE网速。在T-Mobile，网速增长的百分比更高，但其5G网速中值25Mbps仍无法与竞争对手的4G LTE网速竞争。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">另一家公司Opensignal表示，5月至8月间，美国5G手机的平均下载速度比4G手机的下载速度快76%。但美国人使用5G手机的整体下载速度仅为33兆比特/秒，是世界上第二慢的。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">Verizon确实宣称其5G网络速度极快：“比今天的4G快25倍”，但截至今年夏天，美国只有不到1%的地区可以使用到这种快25倍的5G网络。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12433875889/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">不为消费者所熟知的是，5G有低、中、高三个层次。每个频段都需要在正确的位置接入美国联邦通信委员会和蜂窝基站的无线电频谱。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">目前，AT&amp;T和T-Mobile主要使用低频带。这并不是5G蛋糕中最好的那部分——它的下载速度并不比现在共享频谱的4G LTE网络快多少。然而，低频带5G允许信号传播得最远，穿透墙壁。低频带的建设使得T-Mobile和AT&amp;T声称他们的5G是“全国性的”，服务于至少2亿用户。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">今年1月，当《个人电脑杂志》（PC Magazine）就低频带5G网络的缺陷向美国电话电报公司（AT&amp;T）发起挑战时，该公司对网速的表态显得轻描淡写。AT&amp;T副总裁戈登·曼斯菲尔德当时表示：“我们在一开始就明确表示没有太大的区别。”</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">美国最大的无线运营商Verizon也宣布将在今年年底推出低频带5G网络。至少对投资者来说，他们家的首席执行官还直言不讳地表示，全国范围内的5G将比4G带来更大的改进。汉斯 卫翰思5月在摩根大通的一次会议上表示：“一开始，规模会很小。”</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12433875885/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">但世界上很多地方都在使用5G的中频段。它的下载速度没有那么快，但确实提供了更令人印象深刻的下载速度，速度在300 Mbps左右。T-Mobile在收购Sprint的过程中获得了一些中频段频谱，并将其建设成自己的网络。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">5G体验最佳的是高频段，也被称为毫米波频谱。速度令人叹为观止：几秒钟就能下载一季《权力的游戏》。但是，要使用高频段5G，用户不能走很远。Verizon的5G技术几乎完全依赖于市中心和竞技场等密集公共空间的高频带。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">在美国人等待更多的中、高频段5G时，他们已经使用的低频段5G有时不得不面对一个5G手机有时不如4G手机好用的尴尬。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><img src=\"https://inews.gtimg.com/newsapp_bt/0/12433875901/1000\" class=\"content-picture\" style=\"margin: 0.6em auto; padding: 0px; border: 0px none; vertical-align: middle; max-width: 100%; display: block;\"></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">Rootmetrics在其全国测试中也提到了一个问题。如果5G手机提供的是“混合式”服务，在5G信号不强的时候，会降到4G，其下载速度会下降到普通4G LTE速度的一半左右。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">迄今为止，美国三大运营商还没有对5G网络收取额外费用，仅仅是要求消费者购买新智能手机来使用新的网络。但买家要小心了：一些即将推出的机型可能会忽略连接高频段的功能。在购买之前，一定要询问手机是否可以接入运营商所有的5G频段。</span></p>', 2, 1617244967),
(7, 2, '三星与SK海力士将停止向华为出售芯片，9月15日暂停交易', '关键字1,关键字2,关键字3', '说明演示文字7', '美国科技媒体The Verge 9日表示，随着美政府加强对中国手机制造商华为的制裁，三星和SK海力士将停止向华为出售零部件。', '0', 'http://demo.wuwenhui.cn/uploads/20200909/9b377840ff5ab8d57ffda7a9b8116326.png', 1, '2020-09-09', '<p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">美国科技媒体The Verge 9日表示，随着美政府加强对中国手机制造商华为的制裁，三星和SK海力士将停止向华为出售零部件。</span></p><p class=\"one-p\" style=\"margin: 0px 0px 2em; padding: 0px; line-height: 2; overflow-wrap: break-word; color: rgb(0, 0, 0); font-family: &quot;Microsoft Yahei&quot;, Avenir, &quot;Segoe UI&quot;, &quot;Hiragino Sans GB&quot;, STHeiti, &quot;Microsoft Sans Serif&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 18px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-decoration-style: initial; text-decoration-color: initial;\"><span style=\"font-family: &quot;微软雅黑&quot;; font-size: 14px;\">根据朝鲜日报和其他韩国媒体报道，两家公司将于9月15日暂停交易，这一天新的一套规则限制了与华为的交易。</span></p>', 1, 1617244974);
